import {data} from "../../data";
import { useParams } from "react-router-dom";

const ProductDetail = () => {
    let {id} = useParams ();
    let productselect = data.find (dato => dato.id === id)
    return (
        <div className="card" key={dato.id}>                    
        <img src = {dato.img} alt = "imagen producto" />
        <h3>{dato.nombre}</h3>
        <h4>${dato.precio}</h4>
        <h4>{dato.cantidad}</h4>
        <Link to = {`/category/${dato.id}`} >Ver Detalle</Link>
        <button>Comprar</button>
    </div>
    )
};

export default ProductDetail;
