import ItemDetail from "../ItemDetail/ItemDetail";
import { useState, useEffect } from "react";

import { useParams } from "react-router-dom";

export const producto = [
  {id: 1, nombre: "Supreme Plus", precio: 50, img: "../public/imagenes/supremePlus.webp", categoria: "crema", cantidad: 1},
  {id: 2, nombre: "Revitalift", precio: 100, img: "../public/imagenes/cremaRevitalift.webp", categoria: "crema", cantidad: 1},
  {id: 3, nombre: "Garnier", precio: 150, img: "../public/imagenes/garnier.webp", categoria: "locion", cantidad: 1},
  {id: 4, nombre: "Agnes", "precio": 200, img: "../public/imagenes/agnes.jpg", categoria: "locion", cantidad: 1},
  {id: 5, nombre: "Antiarrugas", "precio": 250, img: "../public/imagenes/cremaAntiarrugas.webp", categoria: "crema", cantidad: 1},
  {id: 6, nombre: "Vichy", precio: 300, img: "../public/imagenes/vichy.jpg", categoria: "locion", cantidad: 1}
];


export const ItemDetailContainer = () => {
    const [data, setData] = useState ({});
    const { detalleid } = useParams();

    useEffect(() => {
        const getData = new Promise (resolve => {
            setTimeout (() => {
                resolve (producto);
            }, 1000);
        });
        getData.then (res => setData (res.find (producto => producto.id === parseInt (detalleid))));
    }, [detalleid]);

  return (
    <ItemDetail data = {data} />
  )
}

export default ItemDetailContainer
