import { Link } from "react-router-dom";
import {data} from "../../../public/";
import "./products.css";

const Products = () => {
    return (
        <div>
            {
                data.map (dato => (
                        <div className="card" key={dato.id}>                    
                            <img src = {dato.img} alt = "imagen producto" />
                            <h3>{dato.nombre}</h3>
                            <h4>${dato.precio}</h4>
                            <h4>{dato.cantidad}</h4>
                            <Link to = {`/category/${dato.id}`} >Ver Detalle</Link>
                            <button>Comprar</button>
                        </div>
                    )
                )
            }
        </div>
    )
}

export default Products;
