import { useParams } from "react-router-dom";
import {data} from "../../data";

const ProductDetail = () => {
    let {id} = useParams ();
    let productselect = data.find (dato => dato.id === id)
    return (
        <div className="card" key={productselect.id}>
            <img src = {productselect.img} alt = "imagen producto" />
            <h3>{productselect.nombre}</h3>
            <h4>${productselect.precio}</h4>
            <h4>{productselect.cantidad}</h4>
            <button>Comprar</button>
        </div>
    )
};

export default ProductDetail;
